mapper.select:
select li.id, li.student_id, li.audit_title, li.days, li.status, li.create_date, li.images, li.teacher_id, li.audit_date, si.code as studentCode, si.name as studentName, si.images as studentImages, ti.name as teacherName, ti.images as teacherImages, ti.phone from leave_info li left join student_info si on (si.id = li.student_id) left join teacher_info ti on (ti.id = li.teacher_id) where 1 = 1
AND li.status = #{leaveInfo.status}
AND si.name LIKE CONCAT('%',#{leaveInfo.studentName},'%')
AND ti.name LIKE CONCAT('%',#{leaveInfo.teacherName},'%')
AND ti.user_id = #{leaveInfo.teacherId}
order by li.create_date desc
